mapper.insert:
INSERT
INTO ORDER_INFO(
	ORDER_NO
	,USER_NO
	,ORDER_RE
	,ORDER_PHONE
	,ORDER_ADD
	,ORDER_REQ
	,ORDER_STATUS
	,INTERIOR_NO
	,ORDER_NUMBER
	,POSTCODE
	,ORDER_DATE
	,SUMPRICE
	,ORDER_ADD2
	,SENDER_NAME
	,SENDER_PHONE
	,SENDER_EMAIL
	)
VALUES(
	SEQ_ORDER_NO.NEXTVAL
	,#{userNo}
	,#{orderRe}
	,#{orderPhone}
	,#{roadAddress}
	,#{orderReQ}
	,#{orderStatus}
	,#{interiorNo}
	,#{orderNumber}
	,#{postcode}
	,SYSDATE
	,#{sumPrice}
	,#{detailAddress}
	,#{senderName}
	,#{senderPhone}
	,#{senderEmail}
	)

mapper.update:
UPDATE 
	ORDER_INFO
SET 
	ORDER_STATUS = #{orderStatus}
WHERE
	ORDER_NO = #{orderNo}

UPDATE 
	ORDER_INFO
SET
	ORDER_RE = #{orderRe}
	,ORDER_PHONE = #{orderPhone}
	,ORDER_ADD = #{roadAddress}
	,ORDER_ADD2 = #{detailAddress}
	,ORDER_REQ = #{orderReQ}
	,POSTCODE = #{postcode}
WHERE
	ORDER_NO = #{orderNo}

mapper.insert:
INSERT
INTO REVIEW(
	REVIEW_NO
	,INTERIOR_NO
	,USER_NO
	,REVIEW_CONTENT	
)
