mapper.insert:
INSERT
	INTO ROOM(
				ROOM_NO
				,HOUSE_NO
				,ROOM_NAME
				,GENDER
				,PEOPLE
				,AREA
				,DEPOSIT
				,RENT
				,EXPENSE
				,UTILITY
				,AVAILABLE_DATE
				,STATUS
			)
	VALUES(
				 SEQ_ROOM_NO.NEXTVAL
				,SEQ_HOUSE_NO.CURRVAL
				,#{roomName}
				,#{gender}
				,#{people}
				,#{area}
				,#{deposit}
				,#{rent}
				,#{expense}
				,#{utility}
				,#{availableDate}
				,DEFAULT
			)

			INSERT 
			INTO REPORT(
				REPORT_NO,
				HOUSE_NO,
				REPORT_CATE,
				USER_NO
			)
			VALUES(
				SEQ_REPORT_NO.NEXTVAL
				,#{houseNo}
				,#{reportCate}	
				,#{userNo}	
			)

mapper.select:
	  	SELECT FILE_PATH
        FROM HOUSE_FILE
        WHERE HOUSE_NO = #{hno} and ROOM_NO = #{rno}
