mapper.insert:
INSERT INTO
	  HOUSE
	  	    (
	  	      HOUSE_NO
	  	     ,HOUSE_NAME
	  	     ,HOUSE_TITLE
	  	     ,HOUSE_INTRODUCE
	  	     ,SHARE_SPACE
	  	     ,PERSONAL_SPACE
	  	     ,TRAFFIC
	  	     ,CONVENIENCE
	  	     ,CONDITION
	  	     ,STATUS
	  	     ,REPORT
	  	     ,ADDRESS
	  	     ,LATITUDE
	  	     ,LONGITUDE
	  	     ,USER_NO
	  	    )
	 VALUES
	 		(
	 		  SEQ_HOUSE_NO.NEXTVAL
	 		 ,#{houseName}
	 		 ,#{houseTitle}
	 		 ,#{houseIntroduce}
	 		 ,#{shareSpace}
	 		 ,#{personalSpace}
	 		 ,#{traffic}
	 		 ,#{convenience}
	 		 ,DEFAULT
	 		 ,DEFAULT
	 		 ,DEFAULT
	 		 ,#{address}
	 		 ,#{latitude}
	 		 ,#{longitude}
	 		 ,#{userNo}
	 		)

  		INSERT
			INTO HOUSE_FILE(
								 FILE_NO
								,HOUSE_NO
								
									
										,ROOM_NO
									
									
									
								
								,ORIGIN_NAME
								,CHANGE_NAME
								,FILE_PATH
								,FILE_LEVEL
							)
					VALUES(
								 SEQ_HOUSE_FILE_NO.NEXTVAL
								,SEQ_HOUSE_NO.CURRVAL
								
									
										,#{roomNo}
									
									
									
								
								,#{originName}
								,#{changeName}
								,#{filePath}
								,#{fileLevel}
							)

mapper.update:
  		UPDATE
  				 HOUSE
  		SET
  				 HOUSE_NAME = #{houseName}
  				,HOUSE_TITLE = #{houseTitle}
  				,HOUSE_INTRODUCE = #{houseIntroduce}
  				,SHARE_SPACE = #{shareSpace}
				,PERSONAL_SPACE = #{personalSpace}
				,TRAFFIC = #{traffic}
				,CONVENIENCE = #{convenience}
				,CONDITION = #{condition}
				,ADDRESS = #{address}
				,LATITUDE = #{latitude}
				,LONGITUDE = #{longitude}
		WHERE
				 HOUSE_NO = #{houseNo}
